--
-- Name: eval_template(text, character varying[]); Type: FUNCTION; Schema: fhir; Owner: -
--

CREATE FUNCTION eval_template(_tpl text, VARIADIC _bindings character varying[]) RETURNS text
    LANGUAGE plpgsql
    AS $$
DECLARE
result text := _tpl;
BEGIN
  FOR i IN 1..(array_upper(_bindings, 1)/2) LOOP
    result := replace(result, '{{' || _bindings[i*2 - 1] || '}}', _bindings[i*2]);
  END LOOP;
  RETURN result;
END
$$;


